import React from 'react';
import BookForm from './BookForm';

const AddBook = ({ history }) => {
    const {books, setBooks} = useContext
    const handleOnSubmit = (book) => {
        setBooks([book, ...books]);
        history.pushState('/');
    };

    return (
        <React.Fragment>
            <BookForm handleOnSubmit={handleOnSubmit} />
       </React.Fragment> 
    );
};

export default AddBook;
